mapper.update:
UPDATE products
   SET name = #{name}
      ,categories_id = #{categoriesId}
      ,options_id = #{optionsId}
      ,price = #{price}
      ,description = #{description}
      ,stock_check = #{stockCheck}
      ,product_img = #{productImg}
      ,seq = #{seq}
      ,updated_at = now()
WHERE id = #{id}

UPDATE products
SET seq = #{seq}
WHERE id = #{id}
